import React, { useState } from 'react'
import { Link } from 'react-router-dom';
import { FaBackward } from "react-icons/fa";

const EmployeeForm = () => {
  const [formData, setFormData] = useState({
    Name: "",
    Email: "",
    Title: "",
    department: "",
    role: "",
  });

  const changeHandler = (event) => {
    const { name, value } = event.target;
    setFormData(() => {
      return {
        ...formData,
        [name]: value
      }
    })
  }
  const submitHandler = (event) => {
    event.preventDefault();
    console.log(formData);
  }
  return (
    <div>
      <div className='flex flex-col bg-blue w-full mx-[150px] mt-[100px]'>
        {/* employee */}
        <div className='flex flex-col justify-center items-start '>
          <p className='font-bold text-3xl'>Create a Employee</p>
          <Link to='/'>
            <div className='flex items-center text-blue-800 justify-center gap-3'>
              <FaBackward />

              <p className='font-medium  text-lg'>Back to all Employee List</p>

            </div>

          </Link>
        </div>

        <form onSubmit={submitHandler}>
        {/* employee name */}
          <div>
            <label>Employee Name</label>
            <input
              placeholder='Enter Your Full Name'
              value={formData.Name}
              name='Name'
              onChange={changeHandler}
            />
          </div>
          {/* email id */}
          <div>
            <label>Employee Email Id</label>
            <input
              placeholder='Enter Your Email Id'
              value={formData.Name}
              name='Name'
              onChange={changeHandler}
            />
          </div>
{/* title */}
          <div>
            <label>Employee Title </label>
            <input
              placeholder='Enter Your Employee Title'
              value={formData.Name}
              name='Name'
              onChange={changeHandler}
            />
          </div>
          {/* department */}
          <div>
            <label>Employee Department </label>
            <input
              placeholder='Enter Your Employee Department'
              value={formData.Name}
              name='Name'
              onChange={changeHandler}
            />
          </div>
          {/* role */}
          <div>
            <label>Employee Role</label>
            <input
              placeholder='Enter Your Employee Role'
              value={formData.Name}
              name='Name'
              onChange={changeHandler}
            />
          </div>

        </form>
      </div>

    </div>
  )
}

export default EmployeeForm
