import React, { useEffect } from 'react'
import { Link } from 'react-router-dom';

const Home = () => {

    let getAllUser=async()=>{
        try {
            let url='http://localhost:4000/api/v1/getAllUsers';
            
            const data= await fetch(url,
                {
                    method: "GET",
                    headers: {
                      "Content-Type": "application/json",
                    },
                  });
            const res=await data.json();
            console.log(res);
        } catch (error) {
            
        }
    }
     useEffect
     (()=>{
        getAllUser();
     },[])

    return (
        <div >

            <div className='flex justify-between px-10 items-center '>
                <div className=' pb-5 pt-4'>
                    <h1 className='font-medium text-xl pb-2'>Employees</h1>
                    <p>This is a list of all employees. You can add new employees, edit or delete existing ones.</p>
                </div>
               <Link to="/employee">
                <button className='bg-blue-500 w-[150px] h-[40px] rounded-lg' >Add Employee</button>
                   
               </Link>
            </div>

            <div className='flex items-center justify-center  w-full h-full '>
                <table className=' flex flex-col divide-y divide-blue-800 font-thin px-10  w-full h-full '>
                    <thead className='w-full'>
                        <tr className='flex flex-row justify-around bg-blue-500  py-4 w-full  rounded-t-md'>
                            <th>Employee</th>
                            <th>Title</th>
                            <th>Role</th>
                        </tr>
                    </thead>

                    <tbody>

                        <tr className='flex flex-row justify-around  bg-blue-300  border-[1px]  py-4 w-full h-full rounded-b-md'>
                            <td className='flex items-start justify-between  w-[100px]'>
                                <p>image</p>
                                <div>
                                    <p>name</p>
                                    <p>email</p>
                                </div>

                            </td>
                            <td className='flex flex-col justify-center items-center'>
                                <p>Title</p>
                                <p>department</p>
                            </td>
                            <td>Role</td>
                        </tr>

                    </tbody>


                </table>

            </div>
        </div>
    )
}

export default Home
