import './App.css';
import React, { useState } from "react";
import Home from "./Pages/Home";
import Employee from "./Pages/Employee";
import { Route, Routes } from "react-router-dom";
// require("dotenv").config({path: '/path/to/.env'});


function App() {


  const [toggleDarkMode,setToggleDarkMode]=useState("dark");

  const toggleDarkTheme=()=>{
    toggleDarkMode==="dark"?setToggleDarkMode("light"):setToggleDarkMode("dark");
    console.log(toggleDarkMode);

  }



  return (

      <div className={` w-screen h-screen  ${toggleDarkMode} `}>
        <button className='absolute right-[100px] bg-slate-700 mt-5 ml-[100px] dark:bg-white' onClick={toggleDarkTheme}>Toggle Mode</button>

        <Routes>

          <Route path='/' element={<Home  />} />
          <Route path='/employee' element={<Employee/>} />

        </Routes>

      </div>


  );
}

export default App;
